import React, { useState, useEffect } from 'react';
import { fetchTournaments } from './tournamentService';

const TournamentList = () => {
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getTournaments = async () => {
      try {
        const tournaments = await fetchTournaments();
        setTournaments(tournaments);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    getTournaments();
  }, []);


  if (error) {
    return <p>Error: {error}</p>;
  }

  if (tournaments.length === 0) {
    return <p>No upcoming tournaments found.</p>;
  }

  return (
    <div>
      <h1>Tournaments</h1>
      <ul>
        {tournaments.length > 0 ? (
          tournaments.map(tournament => (
            <li key={tournament.id}>
              <h2>{tournament.name}</h2>
              <p>League: {tournament.league}</p>
              <p>Prizepool: ${tournament.prizepool}</p>
              <p>Start Date: {new Date(tournament.begin_at).toLocaleDateString()}</p>
              <p>End Date: {new Date(tournament.end_at).toLocaleDateString()}</p>
            </li>
          ))
        ) : (
          <p>No tournaments found.</p>
        )}
      </ul>
    </div>
  );
};

export default TournamentList;
