import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './GamesNews.css'; // Import the CSS file

const GamesNews = () => {
    const [news, setNews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const apiKey = 'bd158c9a65504f5786aa5ca60bf0bc21'; // Your NewsAPI Key
    const games = [
        'League of Legends',
        'Warzone',
        'Valorant',
        'Fortnite'
    ];

    const trustedSources = ['IGN', 'GameSpot', 'Kotaku', 'Polygon', 'PC Gamer']; // Trusted sources

    useEffect(() => {
        const fetchNewsForGames = async () => {
            try {
                const newsPromises = games.map(game =>
                    axios.get('https://newsapi.org/v2/everything', {
                        params: {
                            q: game,
                            language: 'en',
                            apiKey: apiKey
                        }
                    })
                );

                const responses = await Promise.all(newsPromises);

                let allArticles = responses.reduce((acc, response) => {
                    let articles = response.data.articles;

                    // Apply filters
                    articles = filterByDate(articles);
                    articles = filterBySource(articles);

                    return acc.concat(articles.slice(0, 5)); // Limit to top 5 articles per game
                }, []);

                // Sort articles by date
                allArticles = allArticles.sort((a, b) => new Date(b.publishedAt) - new Date(a.publishedAt));

                setNews(allArticles);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch news');
                setLoading(false);
            }
        };

        fetchNewsForGames();
    }, []);

    const filterByDate = (articles, daysAgo = 7) => {
        const cutoffDate = new Date();
        cutoffDate.setDate(cutoffDate.getDate() - daysAgo);

        return articles.filter(article => {
            const articleDate = new Date(article.publishedAt);
            return articleDate >= cutoffDate;
        });
    };

    const filterBySource = (articles) => {
        return articles.filter(article => trustedSources.includes(article.source.name));
    };

    if (loading) {
        return <p>Loading news...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div className="games-news-container">
            <h2>Latest News</h2>
            <div className="news-grid">
                {news.length > 0 ? (
                    news.map((article, index) => (
                        <div key={index} className="news-card">
                            {article.urlToImage && (
                                <img src={article.urlToImage} alt={article.title} className="news-image" />
                            )}
                            <div className="news-content">
                                <a href={article.url} target="_blank" rel="noopener noreferrer">
                                    <h3 className="news-title">{article.title}</h3>
                                </a>
                                <p className="news-description">{article.description}</p>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No news available.</p>
                )}
            </div>
        </div>
    );
};

export default GamesNews;
